import styled from '@emotion/styled'

const UploadDiv = styled.div`
    width: 100%;
    margin-top: 1rem;
    margin-botton: 1rem;

`

const UploadForm = styled.div`
    width: 80%;
    margin: 0 auto;
    display: flex;
    flex-direction: column;
    input {
        border-radius: 10px;
        border: 2px solid #c6c6c6;
        padding: 10px;
        &:active,
        &:focus {
            outline: none;
        }
    }
    textarea {
