import React from "react";
import { useState, useEffect } from "react";
import { UploadDiv, UploadForm, UploadButtonDiv } from "../Style/UploadCss.js";
import axios from 'axios';

const Upload = (props) => {
  const [Title, setTitle] = useState("");
  const [Content, setContent] = useState("");

  const onSubmit = () => {
    if (Title === "" || Content === "") {
      return alert("Please fill out both the title and the content!");
    }
    let body = {
      title: Title,
      content: Content,
    }
    axios.post("/api/post/submit", body).then((response) => {
      if (response.data.success){
        alert("Successfully created the post");
      }else {
        alert("Failed to create the post");
      }
    }).catch((err)=>{
      console.log(err);
    })
  };

  return (
    <UploadDiv>
      <UploadForm>
        <label htmlFor="">Title</label>
        <input
          id="title"
          type="text"
          value={Title}
          onChange={(e) => {
            setTitle(e.currentTarget.value);
          }}
        />
        <label htmlFor="">Content</label>
        <textarea
          value={Content}
          onChange={(e) => {
            setContent(e.currentTarget.value);
          }}
        />
        <UploadButtonDiv>
          <button
            onClick={(e) => {
              onSubmit();
            }}
          >
            Submit
          </button>
        </UploadButtonDiv>
      </UploadForm>
    </UploadDiv>
  );
};

export default Upload;
